import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
    },
  },
  paper: {
    padding: theme.spacing(2),
    width: '50%',
    height: '80%',
    margin: 'auto'
  },
  form: {
    alignItems: 'center',
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    flexDirection: 'column'
  },
  //this is for the image to be converted to base64 string
  fileInput: {
    width: '97%',
    margin: '10px 0',
  },
  buttons: {
    marginBottom: 10,
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-around'
  },
  buttonSubmit:{
    width: '100px'
  }
}));
