import React from 'react';
import {Container, AppBar, Typography, Grow, Grid} from '@material-ui/core'; //Grid is used for a simple animation. you may not need it
import Carousel from 'react-material-ui-carousel'; //I read documentation separately
import { Paper, Button } from '@mui/material'; //to test out Carousel

//This is just to test the Carousel
var items = [
    {
        name: "Random Name #1",
        description: "Probably the most random thing you have ever seen!"
    },
    {
        name: "Random Name #2",
        description: "Hello World!"
    }
]

//To test Carousel
function Item(props)
{
    return (
        <Paper>
            <h2>{props.item.name}</h2>
            <p>{props.item.description}</p>

            <Button className="CheckButton">
                Check it out!
            </Button>
        </Paper>
    )
}

function Posts() {
    return (
        <Container maxwidth="lg">
            <AppBar color="inherit" position="static">
                <Typography variant="h2" aligh="left">WIT Blog</Typography>
            </AppBar> 

            <Carousel>
            {
                items.map( (item, i) => <Item key={i} item={item} /> )
            }
        </Carousel>
        </Container>
    )
}

export default Posts
