    const posts = useSelector((state) => state.posts); //we know it's called posts from the reducers folder, index.js file, combineReducers func.
    console.log(posts);
    return (
        <div>
            <h1>POSTS</h1>
            <Post />
            <Post />
        </div>
    )
}

export default Posts
