//reducers

//renamed state to posts because the state here is always somep posts
const reducer = (posts = [], action) => {
    
    switch(action.type){
        case 'FETCH_ALL': 
            return posts;

        case 'CREATE': 
            return posts;

        default: break;
    }
}
