import React, {useState} from 'react';
import {TextField, Button, Typography, Paper} from '@material-ui/core';
import FileBase from 'react-file-base64';
import useStyles from './stylesForm';

function Form() {

    const [postData, setPostData] = useState({
        creator:'',
        title:'',
        details:'',
        deadline:'',
        tags:'',
        selectedFile:'' //this is for the image which will be converted to a base64 string
    })
    
    const classes = useStyles();
    const handleSubmit = () =>{

    }

    const clear = () =>{

    } 

    return (
        <div id="form">
            <Paper className={classes.paper}>
                <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
                    <Typography variant="h6">Creating a post</Typography>
                    <TextField
                        name="creator"
                        variant="outlined"
                        label="Creator"
                        fullwidth
                        value={postData.creator}
                        onChange={(e) => setPostData({...postData, creator: e.target.value})}
                    />

                    <TextField
                        name="title"
                        variant="outlined"
                        label="Title"
                        fullwidth
                        value={postData.title}
                        onChange={(e) => setPostData({...postData, title: e.target.value})}
                    />

                    <TextField
                        name="details"
                        variant="outlined"
                        label="Details"
                        fullwidth
                        value={postData.details}
                        onChange={(e) => setPostData({...postData, details: e.target.value})}
                    />

                    <TextField
                        name="tags"
                        variant="outlined"
                        label="Tags"
                        fullwidth
                        value={postData.tags}
                        onChange={(e) => setPostData({...postData, tags: e.target.value})}
                    />

                    <TextField
                        name="deadline"
                        variant="outlined"
                        label="Deadline"
                        fullwidth
                        value={postData.deadline}
                        onChange={(e) => setPostData({...postData, deadline: e.target.value})}
                    />

                    <div className={classes.fileInput}>
                        <FileBase 
                            type="file"
                            multiple={false}
                            onDone={({base64}) => setPostData({ ...postData, selectedFile: base64 })}
                        />
                    </div>

                    <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullwidth>
                        Add Post
                    </Button>
                    <Button variant="contained" color="secondary" size="large" type="submit" onClick={clear} fullwidth>
                        Clear
                    </Button>

                </form>
            </Paper>
        </div>
    )
}

export default Form;

